import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { api, handleApiError } from '../utils/api';

const EditRecipe = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [fetchLoading, setFetchLoading] = useState(true);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    ingredients: '',
    instructions: '',
    image_url: ''
  });

  useEffect(() => {
    fetchRecipe();
  }, [id]);

  const fetchRecipe = async () => {
    try {
      const response = await api.getRecipe(id);
      const recipe = response.data;
      setFormData({
        title: recipe.title,
        description: recipe.description,
        ingredients: recipe.ingredients.join('\n'),
        instructions: recipe.instructions.join('\n'),
        image_url: recipe.image_url
      });
    } catch (error) {
      alert(handleApiError(error));
      navigate('/');
    } finally {
      setFetchLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.title || !formData.description || !formData.ingredients || !formData.instructions) {
      alert('Please fill in all required fields! üìù');
      return;
    }

    setLoading(true);
    try {
      const recipeData = {
        title: formData.title,
        description: formData.description,
        ingredients: formData.ingredients.split('\n').filter(item => item.trim()),
        instructions: formData.instructions.split('\n').filter(item => item.trim()),
        image_url: formData.image_url
      };

      await api.updateRecipe(id, recipeData);
      navigate(`/recipe/${id}`);
    } catch (error) {
      alert(handleApiError(error));
    } finally {
      setLoading(false);
    }
  };

  if (fetchLoading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="loading-icon">‚è≥</div>
          <div className="loading-text">Loading recipe for editing...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <Link to={`/recipe/${id}`} className="modern-btn modern-btn-outline">
        <span>‚Üê</span>
        Back to Recipe
      </Link>

      <div className="hero-section" style={{ marginTop: '2rem' }}>
        <span className="hero-icon">‚úèÔ∏è</span>
        <h1 className="modern-title" style={{ marginBottom: '1rem' }}>Edit Recipe</h1>
        <p className="hero-text">
          Update your recipe details below. Make it even more awesome! üåü
        </p>
      </div>

      <form onSubmit={handleSubmit} className="modern-form">
        <div className="form-group">
          <label className="form-label">Recipe Title *</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            placeholder="e.g., Grandma's Secret Pancakes"
            className="modern-input"
            required
          />
        </div>

        <div className="form-group">
          <label className="form-label">Description *</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            placeholder="Tell us about your recipe. What makes it special?"
            className="modern-textarea"
            rows="3"
            required
          />
        </div>

        <div className="form-group">
          <label className="form-label">Image URL (optional)</label>
          <input
            type="url"
            name="image_url"
            value={formData.image_url}
            onChange={handleInputChange}
            placeholder="https://example.com/your-recipe-photo.jpg"
            className="modern-input"
          />
          <small className="modern-text" style={{ 
            fontSize: '0.875rem', 
            color: 'var(--text-light)', 
            display: 'block', 
            marginTop: '0.5rem' 
          }}>
            üì∏ Update the link to your recipe photo
          </small>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem' }}>
          <div className="ingredient-list">
            <div className="form-group">
              <label className="form-label" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <span>ü•ò</span>
                Ingredients *
              </label>
              <textarea
                name="ingredients"
                value={formData.ingredients}
                onChange={handleInputChange}
                placeholder="1 cup flour&#10;2 eggs&#10;1 cup milk&#10;1 tsp salt"
                className="modern-textarea"
                rows="8"
                required
              />
              <small className="modern-text" style={{ 
                fontSize: '0.875rem', 
                color: 'var(--text-light)', 
                display: 'block', 
                marginTop: '0.5rem' 
              }}>
                üìù Write each ingredient on a new line
              </small>
            </div>
          </div>

          <div className="instruction-list">
            <div className="form-group">
              <label className="form-label" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <span>üë®‚Äçüç≥</span>
                Instructions *
              </label>
              <textarea
                name="instructions"
                value={formData.instructions}
                onChange={handleInputChange}
                placeholder="Mix all dry ingredients in a bowl&#10;Add wet ingredients slowly&#10;Cook on medium heat for 3-4 minutes&#10;Flip and cook for 2 more minutes"
                className="modern-textarea"
                rows="8"
                required
              />
              <small className="modern-text" style={{ 
                fontSize: '0.875rem', 
                color: 'var(--text-light)', 
                display: 'block', 
                marginTop: '0.5rem' 
              }}>
                üìã Write each step on a new line
              </small>
            </div>
          </div>
        </div>

        <div style={{ 
          textAlign: 'center', 
          marginTop: '2rem', 
          display: 'flex', 
          gap: '1rem', 
          justifyContent: 'center', 
          flexWrap: 'wrap' 
        }}>
          <button 
            type="submit" 
            className="modern-btn modern-btn-accent"
            disabled={loading}
            style={{ fontSize: '1.1rem', padding: '1rem 2rem' }}
          >
            <span>{loading ? '‚è≥' : 'üíæ'}</span>
            {loading ? 'Saving...' : 'Save Changes'}
          </button>
          <Link to={`/recipe/${id}`} className="modern-btn modern-btn-outline">
            <span>‚úï</span>
            Cancel
          </Link>
        </div>
      </form>
    </div>
  );
};

export default EditRecipe;
